package com.example.chuyi.pairwise_key_calc;

import android.support.v7.app.AppCompatActivity;
import android.os.Bundle;
import android.view.View;
import android.widget.TextView;

public class MainActivity extends AppCompatActivity {
    static String ss1 = "                                          ";
    static String ss2 = "                                          ";

    static int[] numbers = {1000,5000,10000,50000,100000};
    Mutual_key k;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);
        k = new Mutual_key(ss1,ss2);


    }
    public void sendAll(View view){
        TextView[] t_all = new TextView[5];
        for (int i = 0;i<5;i++){
            String textID = "security1" + "_" + (i);
            int resID = getResources().getIdentifier(textID, "id", getPackageName());
            t_all[i] = (TextView)findViewById(resID);
            String r = k.keys_eval(numbers[i]);
            t_all[i].setText(r);
        }
    }
    public void sendMessage(View view){
        int i = Integer.parseInt(view.getTag().toString().split("_")[1]);
        String textID = "security1" + "_" + (i);
        int resID = getResources().getIdentifier(textID, "id", getPackageName());
        TextView t = (TextView)findViewById(resID);
        String r = k.keys_eval(numbers[i]);
        t.setText(r);

    }
}
